import{_ as e,y as t}from"./app.f561f6dd.js";const n={},a=t('<h1 id="runtime-configurations" tabindex="-1"><a class="header-anchor" href="#runtime-configurations" aria-hidden="true">#</a> Runtime Configurations</h1><blockquote><p>Note: Runtime configurations are designed for developing, testing, and debugging purposes. You shouldn&#39;t need to change anything mentioned in this page in order for NineAnimator to run.</p></blockquote><p>It&#39;s possible to tweak some of NineAnimator&#39;s behaviors through changing environment variables. This is particularly useful for unit testing and debugging purposes.</p><h3 id="environment-variables" tabindex="-1"><a class="header-anchor" href="#environment-variables" aria-hidden="true">#</a> Environment Variables</h3><table><thead><tr><th style="text-align:center;">Variable Name</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:center;"><code>NINEANIMATOR_NO_SETUP_SCENE</code></td><td style="text-align:left;">Set this variable to prevent NineAnimator from displaying the welcome screen at launch.</td></tr><tr><td style="text-align:center;"><code>NINEANIMATOR_NO_ANIMATIONS</code></td><td style="text-align:left;">Set this variable to disable <code>UIView</code> animations in NineAnimator.</td></tr><tr><td style="text-align:center;"><code>NINEANIMATOR_APPEARANCE_OVERRIDE</code></td><td style="text-align:left;">Set this variable to force NineAnimator to switch to a specific appearance at launch. The user&#39;s configuration will not be changed.</td></tr><tr><td style="text-align:center;"><code>NINEANIMATOR_CREATE_DUMMY_RECORDS</code></td><td style="text-align:left;">Set this variable to create a list of dummy recent anime and playback records at launch (DEBUG builds only). The dummy records are appended to the user&#39;s records.</td></tr></tbody></table>',5);function i(r,o){return a}var d=e(n,[["render",i],["__file","runtime.html.vue"]]);export{d as default};
